package arrays;

public class SearchElement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int searchelement=4;
		boolean flag=false;
		int[] a={4};
		for(int i=0;i<a.length;i++)
		{
			if(a[i]==searchelement)
			{
				flag=true;
			}
		}
		System.out.println(flag);
		

	}

}
